/* eslint-disable react/style-prop-object */
import "./App.css";

import React, { useEffect, useState } from "react";

function App() {
  const [user, setUser] = useState([]);
  const [message, setMessage] = useState("");

  const img_URL = "https://image.tmdb.org/t/p/original";
  const API_KEY = "3d0ac201ad49d76eb1e30e54903dcc54";

  const fetchData = () => {
    return fetch(
      "https://api.themoviedb.org/3/search/movie?api_key=" +
        API_KEY +
        "&query=" +
        String(message).replace(/ /g, "+")
    )
      .then((response) => response.json())
      .then((data) => setUser(data.results));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (event) => {
    setMessage(event.target.value);
    setUser([]);
    fetchData();
  };

  return (
    <>
      <nav class="navbar sticky-top bg-body-tertiary">
        <div class="container-md my-2">
          <a class="navbar-brand">Movie Rater</a>
          <form class="d-flex" role="search">
            <input
              class="form-control me-2"
              type="text"
              id="message"
              name="message"
              placeholder="Search"
              onChange={handleChange}
              value={message}
            />
          </form>
        </div>
      </nav>

      <div>
        <p>Current search: {message}</p>

        <div class="container-md">
          {user &&
            user.length > 0 &&
            user.map((userObj, index) => (
              <div class="card my-4 row" key={userObj.original_title}>
                <div class="col-3">
                  <img
                    src={img_URL + userObj.poster_path}
                    class="card-img-top"
                    width="40px"
                    height="60px"
                    alt="..."
                  />
                </div>
                <div class="card-body col-9">
                  <h5 class="card-title">{userObj.original_title}</h5>
                  <p class="card-text">{userObj.overview}</p>
                </div>
              </div>
            ))}
        </div>
      </div>
    </>
  );
}

export default App;
