/* eslint-disable react/style-prop-object */
import "./App.css";
import Movie from "./Movie";

import "react-select-search/style.css";
import { BrowserRouter as Route, Routes, useNavigate } from "react-router-dom";
import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";

function App() {
  const [user, setUser] = useState([]);
  const [idVal, setIdVal] = useState(null);

  const img_URL = "https://image.tmdb.org/t/p/original";
  const API_KEY = "3d0ac201ad49d76eb1e30e54903dcc54";

  const fetchMovie = () => {
    return fetch(
      `https://api.themoviedb.org/3/movie/${idVal}?api_key=${API_KEY}`
    )
      .then((response) => response.json())
      .then((data) => setUser(data));
  };

  return (
    <>
      <Navbar></Navbar>
      <Routes>
        <Route exact path="/" element={<></>} />
        <Route path="/movie/:id" element={<Movie />} />
      </Routes>
    </>
  );
}

export default App;
