import React, { Component } from "react";
import axios from "axios";

class Create extends Component {
  constructor(props) {
    super(props);

    this.state = {
      movieID: props.movieID,
      rating: "",
      reviewTitle: "",
      reviewText: "",
    };
  }

  handleInputChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleRangeChange = (e) => {
    this.setState({
      rating: e.target.value,
    });
  };

  handleSubmit = (e) => {
    e.preventDefault();

    const { movieID, rating, reviewTitle, reviewText } = this.state;

    const book = {
      movieID,
      rating,
      reviewTitle,
      reviewText,
    };

    axios
      .post("http://localhost:1234/create", book)
      .then(() => console.log("Book Created"))
      .catch((err) => {
        console.error(err);
      });
  };

  render() {
    return (
      <div>
        <h3 class="nav-bold">Reviews</h3>
        <div className="container">
          <form onSubmit={this.handleSubmit}>
            <div style={{ width: "30%" }} className="form-group">
              {/* <input
                type="text"
                className="form-control"
                name="rating"
                placeholder="Rate"
                onChange={this.handleInputChange}
              /> */}
              <label for="customRange2" class="form-label">
                Example range
              </label>
              <br></br>
              <label for="customRange2" class="form-label">
                {this.state.rating} stars
              </label>
              <input
                type="range"
                class="form-range"
                // className="form-control"
                name="rating"
                onChange={this.handleRangeChange}
                min="0"
                max="5"
                step="0.5"
                id="customRange2"
              ></input>
            </div>
            <div style={{ width: "30%" }} className="form-group">
              <input
                type="text"
                className="form-control"
                name="reviewTitle"
                placeholder="Review Title"
                onChange={this.handleInputChange}
              />
            </div>
            <br />
            <div style={{ width: "30%" }} className="form-group">
              <input
                type="text"
                className="form-control"
                name="reviewText"
                placeholder="Review"
                onChange={this.handleInputChange}
              />
            </div>
            <br />
            <div style={{ width: "30%" }}>
              <button className="btn btn-success" type="submit">
                Create
              </button>
            </div>
          </form>
        </div>
      </div>
    );
  }
}

export default Create;
