import logo from "./logo.svg";
import "./App.css";

import React, { useEffect, useState } from "react";

function App() {
  const [user, setUser] = useState([""]);
  const [message, setMessage] = useState("");

  const img_URL = "https://image.tmdb.org/t/p/w500";
  const API_KEY = "3d0ac201ad49d76eb1e30e54903dcc54";

  const fetchData = () => {
    return fetch(
      "https://api.themoviedb.org/3/search/movie?api_key=" +
        API_KEY +
        "&query=" +
        message
    )
      .then((response) => response.json())
      .then((data) => setUser(data.results));
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleChange = (event) => {
    let newSearch = String(event.target.value).replace(/ /g, "+");
    setMessage(newSearch);
    fetchData();
  };

  return (
    <div>
      <input
        type="text"
        id="message"
        name="message"
        onChange={handleChange}
        value={message}
      />
      <p>Current search: {message}</p>
      <p>{user}</p>

      {/* <ul>
        {user.map((userObj, index) => (
          <li key={userObj.original_title}>
            <img src={img_URL + userObj.poster_path} alt="" />
            <p>{userObj.original_title}</p>
            <p>{userObj.overview}</p>
          </li>
        ))}
      </ul> */}
    </div>
  );
}

export default App;
