/* eslint-disable react/style-prop-object */
import "./App.css";

import "react-select-search/style.css";

import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

function Movie() {
  const { id } = useParams();
  const [movie, setMovie] = useState([]);

  const API_KEY = "3d0ac201ad49d76eb1e30e54903dcc54";
  const img_URL = "https://image.tmdb.org/t/p/original";

  const fetchMovie = () => {
    return fetch(`https://api.themoviedb.org/3/movie/${id}?api_key=${API_KEY}`)
      .then((response) => response.json())
      .then((data) => setMovie(data));
  };

  useEffect(() => {
    fetchMovie();
  }, [id]);

  return (
    <>
      <div>
        <div class="container container-md">
          <div class="card my-4" key={movie.title}>
            <div class="row ">
              <div class="col-4">
                <img
                  src={img_URL + movie.poster_path}
                  class="card-img-top"
                  alt="..."
                />
              </div>
              <div class="card-body col-8">
                <div class="d-flex justify-content-between align-items-center">
                  <h2 class="card-title">{movie.title}</h2>
                  <h5 class="card-title me-5">
                    {String(movie.release_date).split("-")[0]}
                  </h5>
                </div>
                <div class="mt-4 hstack gap-3 d-flex align-items-center">
                  <p class="card-text align-self-center">
                    {movie.runtime} mins
                  </p>
                  <div class="vr"></div>
                  {movie.genres &&
                    movie.genres.length > 0 &&
                    movie.genres.map((genre, index) => (
                      <p class="align-self-center" key={genre.id}>
                        {genre.name}
                      </p>
                    ))}
                </div>
                <p class="card-text">{movie.overview}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Movie;
